  import React from 'react';
  import { mdx } from '@mdx-js/react';

  export const frontMatter = {
	title: 'Visualizador 3D'
};
export const metadata = {
  "unversionedId": "3D-viewer",
  "id": "3D-viewer",
  "isDocsHomePage": false,
  "title": "Visualizador 3D",
  "description": "Depois de adicionar a tag de script em seu site, os métodos para criar o Visualizador 3D estarão disponíveis por meio do objeto global R2U.",
  "source": "@site/docs/3D-viewer.md",
  "slug": "/3D-viewer",
  "permalink": "/docs/3D-viewer",
  "version": "current",
  "sidebar": "docs",
  "previous": {
    "title": "Inicialização",
    "permalink": "/docs/"
  },
  "next": {
    "title": "Realidade Aumentada",
    "permalink": "/docs/augmented-reality"
  }
};
  /* @jsxRuntime classic */
/* @jsx mdx */

export const toc = [{
  value: 'R2U.viewer.create',
  id: 'r2uviewercreate',
  children: [{
    value: 'Parâmetro popup',
    id: 'parâmetro-popup',
    children: []
  }, {
    value: 'Parâmetro progressBarPosition',
    id: 'parâmetro-progressbarposition',
    children: []
  }, {
    value: 'Parâmetro progressBarColor',
    id: 'parâmetro-progressbarcolor',
    children: []
  }, {
    value: 'Parâmetro poster',
    id: 'parâmetro-poster',
    children: []
  }, {
    value: 'Demonstração do visualizador 3D',
    id: 'demonstração-do-visualizador-3d',
    children: []
  }]
}, {
  value: 'Mudar sku R2U.viewer.create',
  id: 'mudar-sku-r2uviewercreate',
  children: []
}, {
  value: 'R2U.qrCode.create',
  id: 'r2uqrcodecreate',
  children: []
}];

const layoutProps = {
  toc
};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">

    <p>{`Depois de adicionar a tag de script em seu site, os métodos para criar o Visualizador 3D estarão disponíveis por meio do objeto global R2U.`}</p>
    <h2 {...{
      "id": "r2uviewercreate"
    }}>{`R2U.viewer.create`}</h2>
    <div {...{
      "className": "admonition admonition-tip alert alert--success"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M6.5 0C3.48 0 1 2.19 1 5c0 .92.55 2.25 1 3 1.34 2.25 1.78 2.78 2 4v1h5v-1c.22-1.22.66-1.75 2-4 .45-.75 1-2.08 1-3 0-2.81-2.48-5-5.5-5zm3.64 7.48c-.25.44-.47.8-.67 1.11-.86 1.41-1.25 2.06-1.45 3.23-.02.05-.02.11-.02.17H5c0-.06 0-.13-.02-.17-.2-1.17-.59-1.83-1.45-3.23-.2-.31-.42-.67-.67-1.11C2.44 6.78 2 5.65 2 5c0-2.2 2.02-4 4.5-4 1.22 0 2.36.42 3.22 1.19C10.55 2.94 11 3.94 11 5c0 .66-.44 1.78-.86 2.48zM4 14h5c-.23 1.14-1.3 2-2.5 2s-2.27-.86-2.5-2z"
              }}></path></svg></span><inlineCode parentName="h5">{`mobile`}</inlineCode>{` `}<inlineCode parentName="h5">{`desktop`}</inlineCode></h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}></div></div>
    <p>{`É o método para inserir o visualizador 3D no site de acordo com a posição do elemento HTML indicado. `}</p>
    <pre><code parentName="pre" {...{
        "className": "language-typescript"
      }}>{`  //Lembre de substituir pelas informações do seu produto

  //elemento HTML que irá receber o modelo 3D
  const element = document.getElementById('3d-viewer')
  //SKU do produto desejado
  const sku = 'RE000001'
  //nome do produto que será renderizado
  const name = 'Cadeira Eames'
  //visualizador 3D expansível 
  const popup = false
  //posição da barra de carregamento do modelo 3D
  const progressBarPosition = 'middle'
  //inserir uma imagem sobre o modelo 3D durante o carregamento do modelo
  const poster = 'https://real2u-public-assets.s3.amazonaws.com/images/cadeira.png'

  R2U.viewer.create({ element, sku, name, popup, progressBarPosition, poster })
`}</code></pre>
    <h3 {...{
      "id": "parâmetro-popup"
    }}>{`Parâmetro popup`}</h3>
    <p>{`Habilita e desabilita o botão para abrir o visualizador 3D expansível com o modelo.`}</p>
    <div style={{
      display: 'flex',
      flexDirection: 'column'
    }}>
      <div>
        <pre><code parentName="pre" {...{
            "className": "language-typescript"
          }}>{`  interface R2U {
    viewer: {
    create: (params: {
      element: HTMLElement
      sku: string
      name?: string
      popup?: boolean
    }) => Promise<void>
  }
  }
`}</code></pre>
      </div>
      <div>
  <strong> Exemplo do popup: </strong> 
 <p float="left">
    <img src="../../img/3D-viewer-popup.gif" title="Fallback" width="600" />
  </p>
  </div>
    </div>
    <h3 {...{
      "id": "parâmetro-progressbarposition"
    }}>{`Parâmetro progressBarPosition`}</h3>
    <p>{`  Define a posição que o progress bar será inserido.`}</p>
    <div style={{
      display: 'flex',
      flexDirection: 'column'
    }}>
      <div>
        <pre><code parentName="pre" {...{
            "className": "language-typescript"
          }}>{`  interface R2U {
    viewer: {
    create: (params: {
      element: HTMLElement
      sku: string
      progressBarPosition?: 'top' | 'middle' | 'bottom'
    }) => Promise<void>
  }
  }
`}</code></pre>
      </div>
      <div style={{
        marginTop: '20px'
      }}>
  <strong> Exemplo do progressBarPosition no desktop: </strong> 
        <p float="left" style={{
          display: 'flex',
          flexDirection: 'row'
        }}>
  <div style={{
            display: 'flex',
            flexDirection: 'column',
            marginLeft: '15px',
            marginRight: '15px'
          }}>
    <a>top</a>
    <img src="https://real2u-public-assets.s3.amazonaws.com/images/cadeira-progressbar-top.png" title="Progress bar top" height="150" />
  </div>
  <div style={{
            display: 'flex',
            flexDirection: 'column'
          }}>
    <a>middle</a>
    <img src="https://real2u-public-assets.s3.amazonaws.com/images/cadeira-progressbar-middle.png" title="Progress bar middle" height="150" />
  </div>
  <div style={{
            display: 'flex',
            flexDirection: 'column'
          }}>
    <a>bottom</a>
    <img src="https://real2u-public-assets.s3.amazonaws.com/images/cadeira-progressbar-bottom.png" title="Progress bar bottom" height="150" />
  </div>
        </p>
  </div>
    </div>
    <h3 {...{
      "id": "parâmetro-progressbarcolor"
    }}>{`Parâmetro progressBarColor`}</h3>
    <p>{`Define a cor do progressBarPosition`}</p>
    <div>
      <pre><code parentName="pre" {...{
          "className": "language-typescript"
        }}>{`  interface R2U {
    viewer: {
    create: (params: {
      element: HTMLElement
      sku: string
      progressBarColor?: string
    }) => Promise<void>
  }
  }
`}</code></pre>
    </div>
    <h3 {...{
      "id": "parâmetro-poster"
    }}>{`Parâmetro poster`}</h3>
    <p>{`Define uma imagem sobre o modelo 3D que será exibida durante o carregamento do modelo. `}</p>
    <div>
      <div>
        <pre><code parentName="pre" {...{
            "className": "language-typescript"
          }}>{`  interface R2U {
    viewer: {
    create: (params: {
      element: HTMLElement
      sku: string
      poster?: string
    }) => Promise<void>
  }
  }
`}</code></pre>
      </div>
    </div>
    <h3 {...{
      "id": "demonstração-do-visualizador-3d"
    }}>{`Demonstração do visualizador 3D`}</h3>
 <p float="left">
    <img src="../../img/3D-viewer.gif" title="Fallback" width="600" />
  </p>
    <h2 {...{
      "id": "mudar-sku-r2uviewercreate"
    }}>{`Mudar sku R2U.viewer.create`}</h2>
    <p>{`O método setSku atualiza o SKU do produto.`}</p>
    <div>
      <div>
        <pre><code parentName="pre" {...{
            "className": "language-typescript"
          }}>{` // SKU de teste -- lembre de substituir pelas informações do seu produto
  const buttonChangeSku = document.getElementById('button-changeSku')
  const changeSku = document.getElementById('viewer-changeSku')

  // O create irá retornar um objeto que terá o método setSku
  const handler = R2U.viewer.create({
    element: changeSku,
    sku,
    name,
    popup,
    progressBarPosition,
    poster
  })
  //muda o sku no click do botão
  buttonChangeSku.addEventListener('click', () => {
    handler.setSku('RE000002')
  })
`}</code></pre>
      </div>
      <div>
  <strong> Exemplo do setSku: </strong> 
  <p float="left">
    <img src="../../img/3D-viewer-setSku.gif" title="Fallback" width="600" />
  </p>

  </div>
    </div>
    <h2 {...{
      "id": "r2uqrcodecreate"
    }}>{`R2U.qrCode.create`}</h2>
    <div {...{
      "className": "admonition admonition-danger alert alert--danger"
    }}><div parentName="div" {...{
        "className": "admonition-heading"
      }}><h5 parentName="div"><span parentName="h5" {...{
            "className": "admonition-icon"
          }}><svg parentName="span" {...{
              "xmlns": "http://www.w3.org/2000/svg",
              "width": "12",
              "height": "16",
              "viewBox": "0 0 12 16"
            }}><path parentName="svg" {...{
                "fillRule": "evenodd",
                "d": "M5.05.31c.81 2.17.41 3.38-.52 4.31C3.55 5.67 1.98 6.45.9 7.98c-1.45 2.05-1.7 6.53 3.53 7.7-2.2-1.16-2.67-4.52-.3-6.61-.61 2.03.53 3.33 1.94 2.86 1.39-.47 2.3.53 2.27 1.67-.02.78-.31 1.44-1.13 1.81 3.42-.59 4.78-3.42 4.78-5.56 0-2.84-2.53-3.22-1.25-5.61-1.52.13-2.03 1.13-1.89 2.75.09 1.08-1.02 1.8-1.86 1.33-.67-.41-.66-1.19-.06-1.78C8.18 5.31 8.68 2.45 5.05.32L5.03.3l.02.01z"
              }}></path></svg></span>{`Problemas conhecidos: Usuários Android escaneando o QR Code sem ter o browser aberto receberão uma mensagem de fallback avisando que o dispositivo não é suportado.`}</h5></div><div parentName="div" {...{
        "className": "admonition-content"
      }}></div></div>
    <p>{`Cria um QRCode que ao scannear direciona o usuário para o modelo em RA.`}</p>
    <div>
      <div>
        <pre><code parentName="pre" {...{
            "className": "language-typescript"
          }}>{`  const element = document.getElementById('qrCode')
  R2U.qrCode.create({ element, sku })
`}</code></pre>
      </div>
      <div>
  <p float="left">
    <img src="../img/3D-viewer-qrcode.png" title="Fallback" width="200" />
  </p>
  </div>
    </div>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
