import React from 'react';
import {useLocation} from '@docusaurus/router';
import {useThemeConfig} from '@docusaurus/theme-common';
import {DocSidebarItem} from '@theme/DocSidebar';
import {useActivePlugin, useActiveDocSidebar} from '@theme/hooks/useDocs';
export default function DocSidebarNavbarItem() {
  const {sidebarCollapsible} = useThemeConfig();
  const {pathname} = useLocation();
  const {pluginId} = useActivePlugin({
    failfast: true,
  });
  const docSidebar = useActiveDocSidebar(pluginId);
  return (
    <>
      {docSidebar.map((item) => (
        <DocSidebarItem
          key={item.label}
          item={item}
          collapsible={sidebarCollapsible}
          activePath={pathname}
        />
      ))}
    </>
  );
}
